<?php	class JLController {			var $appName;				function JLController($appNameNew) {			$this->setAppName($appNameNew);		}				function setAppName($appNameNew) {			$this->appName = $appNameNew;		}						/*			charge un lment (model, vue, autre...)			$elementType (string): model, view			$elementName (string): nom de l'lment, exemple le model "Toto", ce qui donnera le fichier modelToto.php			$elementCustomName (string): nom  donner  l'lment ajout au controller, exemple modelTotoQuiTue			$admin (bool): composant front-end (false) ou back-end (true) ?			$appLoad (string): nom de l'application  partir de laquelle charger l'lment (par dfaut on prendra l'appli du controller)		*/		function addElement($elementType, $elementName = '', $elementCustomName = '', $admin = false, $appLoad = '') {					// dtermine si c'est une appli front ou back			if(!$admin) { // app cot front				$path	= SITE_PATH;			} else { // app cot back				$path	= SITE_PATH_ADMIN;			}						// appli custom  charger ou celle de l'appli du controller ?			if(!$appLoad) {				$appLoad = $this->appName;			}						// include la view			include_once($path.'/app/app_'.$appLoad.'/'.$elementType.ucwords($elementName).'.php');						// nom custom ?			if(!$elementCustomName) {				$elementCustomName = $elementName ? $elementName : $elementType;			}						// instancie la vue de l'lment			$elementClassName				= $appLoad.ucwords($elementType).ucwords($elementName);			$this->{$elementCustomName} 	= new $elementClassName;					}				// voir dfinition addElement()		function addView($viewName = '', $viewCustomName = '', $admin = false, $appLoad = '') {			$this->addElement('view', $viewName, $viewCustomName, $admin, $appLoad);		}				// voir dfinition addElement()		function addModel($modelName = '', $modelCustomName = '', $admin = false, $appLoad = '') {			$this->addElement('model', $modelName, $modelCustomName, $admin, $appLoad);		}			}	?>